function eshopBannerClose(banner)
{
	BX.ajax.post(
		'/export_votes.php',
		{
			sessid: BX.bitrix_sessid(),
			action: 'eshopBannerClose'
		},
		function(result)
		{
			banner.style.display = "none";
		}
	);
}

